   fMin_1[0][13] = -3.05427694321;
   fMax_1[0][13] = 2.97948527336;
   fMin_1[1][13] = -3.02776956558;
   fMax_1[1][13] = 3.04666376114;
   fMin_1[2][13] = -3.05427694321;
   fMax_1[2][13] = 3.04666376114;
   fMin_1[0][14] = -3.68934881474e+19;
   fMax_1[0][14] = 3.68934881474e+19;
   fMin_1[1][14] = -3.68934881474e+19;
   fMax_1[1][14] = 3.68934881474e+19;
   fMin_1[2][14] = -3.68934881474e+19;
   fMax_1[2][14] = 3.68934881474e+19;
   fMin_1[0][15] = -2.15273094177;
